mapper.update:
UPDATE BOARD SET 
	   BOARD_TITLE = #{boardTitle}
     , BOARD_CONTENT = #{boardContent}


	 , BOARD_ORIGINAL_FILENAME = #{boardOriginalFilename}


	 , BOARD_ORIGINAL_FILENAME = null

	 , BOARD_RENAME_FILENAME =


	 	#{boardRenameFilename}	


	 	null


	 , BOARD_DATE = SYSDATE
 WHERE BOARD_NUM = #{boardNum}

UPDATE BOARD SET 
       BOARD_READCOUNT = BOARD_READCOUNT + 1
 WHERE BOARD_NUM = #{boardNum}
